import {CLIENT_ID} from 'react-native-dotenv'
import axios from 'axios'
import Session from "./Session";


export default class ImgurApi {
    static getFeed(feedName) {
        return axios.get('https://api.imgur.com/3/gallery/t/' + feedName, {
            headers: {
                'Authorization': 'Client-ID ' + CLIENT_ID
            }
        }).then((response) => {
            return response.data;
        }).catch((error) => {
            console.log('error : ' + error);
        });
    }

    static upload(imgBytes) {
        return Session.get().then((session) => {
            return axios({
                method: 'POST',
                url: 'https://api.imgur.com/3/image',
                headers: {
                    'Authorization': 'Bearer ' + JSON.parse(session).access_token
                },
                data: {
                    'image': imgBytes,
                }
            }).then((response) => {
                return response.data;
            }).catch((error) => {
                console.log('error : ' + error);
            });
        });
    }

    static addToFavorite(imgId) {
        return Session.get().then((session) => {
            return axios({
                method: 'POST',
                url: 'https://api.imgur.com/3/image/' + imgId + '/favorite',
                headers: {
                    'Authorization': 'Bearer ' + JSON.parse(session).access_token
                }
            }).then((response) => {
                return response.data;
            }).catch((error) => {
                console.log('error : ' + error);
            });
        });
    }

    static getFavoritesOnPage(page) {
        return Session.get().then((session) => {
            console.log('https://api.imgur.com/3/account/' + JSON.parse(session).account_name + '/favorites/' + page + '/newest');
            return axios({
                method: 'GET',
                url: 'https://api.imgur.com/3/account/' + JSON.parse(session).account_name + '/favorites/' + page + '/newest',
                headers: {
                    'Authorization': 'Bearer ' + JSON.parse(session).access_token
                }
            }).then((response) => {
                console.log("bearer: " + JSON.parse(session).access_token);
                return response.data;
            }).catch((error) => {
                console.log('error : ' + error);
            });
        });
    }

    static getAvatar(accountName) {
        return Session.get().then((session) => {
            return axios({
                method: 'GET',
                url: 'https://api.imgur.com/3/account/' + accountName + '/avatar',
                headers: {
                    'Authorization': 'Bearer ' + JSON.parse(session).access_token
                }
            }).then((response) => {
                return response.data.data.avatar;
            }).catch((error) => {
                console.log('error : ' + error);
            });
        });
    }

    // TODO up/down vote
}

import React, {Component} from 'react';
import {Text, View, FlatList, TextInput, StyleSheet} from 'react-native';
import ImgurApi from './ImgurApi';
import ImgurPost from './ImgurPost';
import wording from './utils/wording'

export default class ImgurFeed extends Component {
    constructor(props) {
        super(props);
        this.state = {
            jsonPosts: [],
            loading: false,
            images: [],
            text: [],
            feedName: '',
        };
    }

    render() {
        return (
            <View style={{justifyContent: 'space-between'}}>
                {/*<View style={{flex: 1, width: 500, height: 500, backgroundColor: 'blue'}}>*/}
                <TextInput
                    style={styles.FeedSearchBar}
                    placeholder={wording.feedPlaceHolder}
                    onSubmitEditing={(t) => this.setState({feedName: t.nativeEvent.text, loading: true})}
                />
                {this.state.text}
                {this.state.images}
            </View>
        );
    }

    componentDidUpdate(prevProps, prevState, snapshot) {
        if (prevState.feedName !== this.state.feedName) {
            ImgurApi.getFeed(this.state.feedName).then((response) => {
                this.setState({
                    jsonPosts: response.data.items,
                    loading: false,
                });
            }, (error) => {
                console.log(error);
            });
        }
        if (prevState.loading !== this.state.loading)
            this.updateImages();
    }

    updateImages() {
        if (this.state.loading) {
            this.setState({
                text: <Text>{wording.loadingImages}</Text>
            });
        } else {
            this.setState({
                text: <Text>{wording.imagesLoaded}</Text>
            });
            this.setState({
                images:
                    <FlatList
                        style={styles.FeedStyle}
                        data={this.state.jsonPosts}
                        initialNumToRender={4}
                        windowSize={5}
                        renderItem={(jsonPost) => <ImgurPost jsonData={jsonPost.item}/>}
                    />
            });
            this.setState({loading: false});
        }
    }
};

